var rescapeRegExp = /([-.*+?\^${}()|\[\]\/\\])/g,
  StringUtil;

StringUtil = module.exports = {};


/**
 * 判断source是否以fragment结尾
 * @param  {String}       source      字符串
 * @param  {String}       fragment    字符串片段
 */
StringUtil.endWith = function(source, fragment) {
  var index;

  if (source == undefined || fragment == undefined) {
    return false;
  }

  index = source.lastIndexOf(fragment);

  return index !== -1 && (index + fragment.length) === source.length;
}

/**
 * 转义字符串中的正则表达式字符
 * @param  {String}       source      字符串
 */
StringUtil.escapeRegExp=function(source){
  return source.replace(rescapeRegExp,'\\$1')
}